import { useContext, useState } from "react";
import {} from "../index";
import { StoreContext } from "../store/StoreContext";

export const LoginForm = () => {
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const { store } = useContext(StoreContext);
  return (
    <section className="flex flex-col">
      <div></div>
      <h3 className="font-bold text-4xl leading-10">Logowanie do oddziału</h3>
      <p className="text-base text-[#A3AED0] tracking-tight">
        Wpisz swój adres e-mail i hasło, aby się zalogować!
      </p>
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="text"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={() => store.login(email, password)}>Log in</button>
    </section>
  );
};
