import { useContext, useState } from "react";
import {} from "../index";
import { StoreContext } from "../store/StoreContext";

export const LoginForm = () => {
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const { store } = useContext(StoreContext);
  return (
    <section className="flex flex-col">
      <div className="text-left">
        <h3 className="font-bold text-4xl leading-10">Logowanie do oddziału</h3>
        <p className="text-base text-[#A3AED0] tracking-tight mt-2  pb-[13px] relative">
          Wpisz swój adres e-mail i hasło, aby się zalogować!
          <span className="absolute bottom-0 left-0 w-full h-[1px] bg-[#E0E5F2]"></span>
        </p>
      </div>
      <form action="" method="POST">
      <select
        id="currency"
        name="currency"
        className="h-full rounded-md border-0 bg-transparent py-0 pl-2 pr-7 text-gray-500 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm"
      >
        <option>USD</option>
        <option>CAD</option>
        <option>EUR</option>
      </select>

      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="text"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={() => store.login(email, password)}>Log in</button>
      </form>


    </section>
  );
};
